INSERT INTO "public"."roles" ("id", "name", "description", "reference") VALUES ('1', 'Software Engineer', 'Software engineering is a branch of both computer science and engineering focused on designing, developing, testing, and maintaining software applications. It involves applying engineering principles and computer programming expertise to develop software systems that meet user needs.

The terms programmer and coder overlap software engineer, but they imply only the construction aspect of a typical software engineer workload.

A software engineer applies a software development process, which involves defining, implementing, testing, managing, and maintaining software systems, as well as developing the software development process itself.', 'https://en.wikipedia.org/wiki/Software_engineering'), ('2', 'DevOps Engineer', 'DevOps is the integration and automation of the software development and information technology operations.', 'https://en.wikipedia.org/wiki/DevOps'), ('3', 'Database Administrator', 'A database administrator (DBA) manages computer databases. The role may include capacity planning, installation, configuration, database design, migration, performance monitoring, security, troubleshooting, as well as backup and data recovery.', 'https://en.wikipedia.org/wiki/Database_administrator'), ('4', 'Database Developer', 'A software developer who writes database-specific code, such as SQL queries.', null), ('5', 'QA Engineer', 'A software engineer focused on writing tests and ensuring the quality of a software system.', null), ('6', 'Software Developer', 'Software development is the process of designing and implementing a software solution to satisfy a user. The process is more encompassing than programming, writing code, in that it includes conceiving the goal, evaluating feasibility, analyzing requirements, design, testing and release. The process is part of software engineering which also includes organizational management, project management, configuration management and other aspects.', 'https://en.wikipedia.org/wiki/Software_development'), ('7', 'Site Reliability Engineer', 'Site Reliability Engineering (SRE) is a discipline in the field of Software Engineering and IT infrastructure support that monitors and improves the availability and performance of deployed software systems and large software services.', 'https://en.wikipedia.org/wiki/Site_reliability_engineering'), ('8', 'Delivery Boy', 'Eats pizza and watches TV on the couch.', 'https://futurama.fandom.com/wiki/Philip_J._Fry');

INSERT INTO "public"."skill_categories" ("id", "name", "parent_skill_category_id", "description", "reference", "notes") VALUES ('1', 'General-purpose programming language', '19', null, null, null), ('2', 'Runtime system', '14', 'A software system that provides an environment in which software programs run.', 'https://en.wikipedia.org/wiki/Runtime_system', 'AKA runtime environment'), ('3', 'Kernel', '22', 'A computer program at the core of a computer''s operating system.', 'https://en.wikipedia.org/wiki/Kernel_(operating_system)', null), ('4', 'Software library', '18', null, null, null), ('5', 'Software framework', '18', 'Software that provides reusable, generic functionality which developers can extend or customize to create complete solutions.

Unlike a library, where user code controls the program''s control flow, a framework implements inversion of control by dictating the overall structure and calling user code at predefined extension points (e.g., through template methods or hooks). Frameworks also provide default behaviors that work out-of-the-box, structured mechanisms for extensibility, and a fixed core that accepts extensions (e.g., plugins or subclasses) without direct modification. ', 'https://en.wikipedia.org/wiki/Software_framework', null), ('6', 'Domain-specific programming language', '19', null, null, null), ('7', 'Markup language', '20', null, null, null), ('8', 'Data-interchange format', '30', null, null, 'Data-interchange format implies a corresponding file format.'), ('9', 'Application protocol', '10', null, null, null), ('10', 'Communication protocol', '30', 'A system of rules that allows two or more entities of a communications system to transmit information via any variation of a physical quantity. The protocol defines the rules, syntax, semantics, and synchronization of communication and possible error recovery methods. Protocols may be implemented by hardware, software, or a combination of both.', 'https://en.wikipedia.org/wiki/Communication_protocol', null), ('11', 'Software platform', '18', 'The infrastructure on which software is executed.', 'https://en.wikipedia.org/wiki/Computing_platform', 'unneeded?'), ('12', 'Database management system', '14', 'A software system that enables users to define, create, maintain and control access to the database.', 'https://en.wikipedia.org/wiki/Database#Database_management_system', null), ('13', 'Computer language', null, null, 'https://en.wikipedia.org/wiki/Computer_language', null), ('14', 'Software system', '18', 'A system of intercommunicating components based on software forming part of a computer system (a combination of hardware and software).
', 'https://en.wikipedia.org/wiki/Software_system', null), ('15', 'Relational database management system', '12', 'A type of database management system that stores data in a structured format using rows and columns.', 'https://en.wikipedia.org/wiki/Relational_database', null), ('16', 'Document database management system', '12', null, null, null), ('17', 'Multi-model database management system', '12', null, null, null), ('18', 'Software', null, null, null, null), ('19', 'Programming language', '13', null, null, null), ('20', 'Structural language', '13', null, null, null), ('21', 'Style sheet language', '20', null, null, null), ('22', 'Software program', '18', 'A sequence or set of instructions in a programming language for a computer to execute. It is one component of software, which also includes documentation and other intangible components.', 'https://en.wikipedia.org/wiki/Computer_program', null), ('23', 'Software programming tool', '22', 'A programming tool or software development tool is a computer program that is used to develop another computer program, usually by helping the developer manage computer files.', 'https://en.wikipedia.org/wiki/Programming_tool', null), ('24', 'Software build tool', '23', null, null, null), ('25', 'System software', '18', 'Software that provides a platform for other software.', 'https://en.wikipedia.org/wiki/System_software', null), ('26', 'Operating system', '14', 'System software that manages computer hardware and software resources, and provides common services for computer programs.', 'https://en.wikipedia.org/wiki/Operating_system', ''), ('27', 'Web development framework', '5', 'A software framework that is designed to support the development of web applications including web services, web resources, and web APIs.', 'https://en.wikipedia.org/wiki/Web_framework', null), ('28', 'CSS framework', '4', 'A library allowing for easier, more standards-compliant web design using the Cascading Style Sheets language.', 'https://en.wikipedia.org/wiki/CSS_framework', null), ('29', 'Middleware', '5', 'A type of software framework that provides services to software applications beyond those available from the operating system. It is sometimes described as "software glue."', 'https://en.wikipedia.org/wiki/Middleware', null), ('30', 'Electronic data interchange', null, 'Electronic data interchange (EDI) is the concept of businesses electronically communicating information that was traditionally communicated on paper, such as purchase orders, advance ship notices, and invoices. Technical standards for EDI exist to facilitate parties transacting such instruments without having to make special arrangements.', 'https://en.wikipedia.org/wiki/Electronic_data_interchange', null);

